var regexp = /[\[\]\{\}\(\)\*\+\?\.\,\^\$\|\#\-]/g
  ;

function escape(text) {
  return text.replace(regexp, '\\$&');
}

exports.escape = escape;

function merge(obj1, obj2) {
  var result = {}
    , objs = [obj1, obj2]
    , obj
    , key
    , i
    ;

  for (i = 0, obj = objs[i]; i <= objs.length; obj = objs[++i]) {
    for (key in obj) {
      if (obj.hasOwnProperty(key)) {
        result[key] = obj[key];
      }
    }
  }

  return result;
}

exports.merge = merge;